        public static class CateListBean implements Comparable<CateListBean>, Parcelable {
            /**
             * cateName : 电子产品
             * sort : 0
             * pic : http://gfs9.gomein.net.cn/T15dxvBsdT1RCvBVdK.png
             * addTime : 2020-08-18
             * isDel : false
             * id : 1
             * cateID : 1
             */

            private String cateName;
            private int sort;
            private String pic;
            private String addTime;
            private boolean isDel;
            private int id;
            private int cateID;

            protected CateListBean(Parcel in) {
                cateName = in.readString();
                sort = in.readInt();
                pic = in.readString();
                addTime = in.readString();
                isDel = in.readByte() != 0;
                id = in.readInt();
                cateID = in.readInt();
            }

            @Override
            public void writeToParcel(Parcel dest, int flags) {
                dest.writeString(cateName);
                dest.writeInt(sort);
                dest.writeString(pic);
                dest.writeString(addTime);
                dest.writeByte((byte) (isDel ? 1 : 0));
                dest.writeInt(id);
                dest.writeInt(cateID);
            }

            @Override
            public int describeContents() {
                return 0;
            }

            public static final Creator<CateListBean> CREATOR = new Creator<CateListBean>() {
                @Override
                public CateListBean createFromParcel(Parcel in) {
                    return new CateListBean(in);
                }

                @Override
                public CateListBean[] newArray(int size) {
                    return new CateListBean[size];
                }
            };

            public String getCateName() {
                return cateName;
            }

            public void setCateName(String cateName) {
                this.cateName = cateName;
            }

            public int getSort() {
                return sort;
            }

            public void setSort(int sort) {
                this.sort = sort;
            }

            public String getPic() {
                return pic;
            }

            public void setPic(String pic) {
                this.pic = pic;
            }

            public String getAddTime() {
                return addTime;
            }

            public void setAddTime(String addTime) {
                this.addTime = addTime;
            }

            public boolean isIsDel() {
                return isDel;
            }

            public void setIsDel(boolean isDel) {
                this.isDel = isDel;
            }

            public int getId() {
                return id;
            }

            public void setId(int id) {
                this.id = id;
            }

            public int getCateID() {
                return cateID;
            }

            public void setCateID(int cateID) {
                this.cateID = cateID;
            }

            @Override
            public int compareTo(CateListBean o) {
                int i = this.getSort() - o.getSort();//先按照年龄排序
                if (i == 0) {
                    return this.id - o.getId();//如果年龄相等了再用分数进行排序
                }
                return i;
            }
        }

